
public class Class2_ChristmasTree_Sanjana {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		int j =1;
		int k =1;
		while(i<=4){
			while(k<=5-i){
				System.out.println(" ");
				k++;
			}
			while(j<=2*i-1){
				
				System.out.print("*");
				j++;
			
			}
			
	        System.out.println();
			i++;
		}

	}

}
